    public Tablero tab;
    public boolean tablerocreado=false;
    private void definirtamActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_definirtamActionPerformed
        // TODO add your handling code here:
        fondo.removeAll();
        fondo.repaint();
        tab=new Tablero(Integer.parseInt(tam.getText()),fondo);
        tablerocreado=true;
    }//GEN-LAST:event_definirtamActionPerformed

    private void moverActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_moverActionPerformed
        // TODO add your handling code here:
        if(!tablerocreado){
            System.out.print("Tablero no creado");
            return;
        }
        Movimiento mov=new Movimiento(Integer.parseInt(cantmov.getText()),tab);
        mov.start();
        
    }//GEN-LAST:event_moverActionPerformed

